WebLink.url:
var records = {!GETRECORDIDS( $ObjectType.Opportunity )} ;
var oName = 'Opportunity';
var error = false;
var msj = '';

if( records.length > 40 ) { 
    error = true; 
    msj ='{!$Label.ap_Errors_moreThan20records}';
}
if( records.length < 1 ){
   error = true;
   msj = '{!$Label.ap_Errors_selectOneRecord}'; 
}

if( error ) {
       alert(msj);    
}
else{
       location.href = '/apex/ActionPlanCreation?refId='+records+'&refType='+oName;
}
